INSERT INTO `ims_han_sheka_zhufu` VALUES ('156', '0', '发送人的名字', '收卡人的名字', '你的出生给这个世界带来了欢笑，你的出生给这个世界带来了祥和，你的出生给这个世界带来了幸福，为了这个世界的美好，你的生日一定要快快乐乐的哟。', '192', '20', '20', '40', '20', '20', '60', '30', '', '', '', '', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('157', '0', '发送人的名字', '收卡人的名字', '来到这世界的30天里你多数时间都在迷恋甜甜的梦乡，粉粉小小的拳头攥成一团，像是握着对这个大大世界的小小不安，可爱的宝贝，我亲亲的将祝福吻上你绵软的脸颊，愿幸福与好运拥抱你甜美的每一天。亲爱的小宝贝，满月快乐！', '193', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('158', '0', '发送人的名字', '收卡人的名字', '当我把神灯擦三下后，灯神问我想许什么愿望？我说：请帮我保佑一个正在看贺卡的人生日快乐，希望那人永远幸福！唱着生日快乐的歌向你走近，任时光匆匆，永不褪色的是感情，烛光闪闪幸福，折一只祝福的纸鹤，吃一块甜蜜的蛋糕，许一个梦想成真的愿望，生日快乐！', '194', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('159', '0', '发送人的名字', '收卡人的名字', '当我把神灯擦三下后，灯神问我想许什么愿望？我说：请帮我保佑一个正在看贺卡的人生日快乐，希望那人永远幸福！唱着生日快乐的歌向你走近，任时光匆匆，永不褪色的是感情，烛光闪闪幸福，折一只祝福的纸鹤，吃一块甜蜜的蛋糕，许一个梦想成真的愿望，生日快乐！', '195', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('160', '0', '发送人的名字', '收卡人的名字', '愿你是风，鼓起白色的帆；愿你是船，劈开蓝色的波澜。生活正在你的前方微笑，勇敢地走上前去，将彩色的人生拥抱。', '196', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('161', '0', '发送人的名字', '收卡人的名字', '你的生日来到，气氛格外美妙，帮你切好蛋糕，愿你欢乐逍遥。发送祝福符号，盼你一切都好，幸福的你一定会年年有今日，岁岁有今朝。', '197', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('162', '0', '发送人的名字', '收卡人的名字', '时间流过沙漏，留下大浪淘沙的沉淀，如果一滴水代表一个祝福，我送你一个东海；如果一颗星代表一份幸福，我送你一条银河；如果一棵树代表一份思念，我送你一片森林。 祝您年年有今日，生日快乐！', '198', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('163', '0', '发送人的名字', '收卡人的名字', '新生宝宝的到来，又为这个世界增添了一份亮丽的色彩，在这样美好的日子里，祝小宝宝健康成长，幸福快乐！快乐日子从此降临，幸福的生活即将开始。也祝你们合家幸福，欢乐不断！', '199', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('164', '0', '发送人的名字', '收卡人的名字', '祝福声声传捷报，君逢荣升寄情调，前途光辉耀日月，程鹏万里搏去霄，似花年岁怀壮志，锦绣前程人逍遥。不懈的努力“升”起了快乐的大旗，无悔的付出“升”起了满心的欢喜，持久的追求“升”起了无边的甜蜜。恭喜你，升职快乐!', '200', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('165', '0', '发送人的名字', '收卡人的名字', '送你一叶扁舟，愿你一帆风顺；送你一支高粱，愿你步步高升；最后送你一颗祝福心，愿你每天都进一步，芝麻开花节节高，前路还长，继续加油，勇攀高峰！恭喜你高升！', '201', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('166', '0', '发送人的名字', '收卡人的名字', '开拓事业的犁铧，尽管如此沉重，但您以非凡的毅力，拥有了自己的事业，毕竟一步一步的走过来了！庆典礼的掌声，化作潇潇春雨，祝您播下美好未来的良种！由衷祝愿您成功！', '202', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('167', '0', '发送人的名字', '收卡人的名字', '恭贺恭祝加恭喜，热情奔放的年纪，热情如火的夏季，热到爆炸的欣喜；真心礼包全送你，愿勇气之花在你心里永开不败。以加冕的名义祝福你，愿岁月老去永不改奋斗的情怀。继续前行！', '203', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('168', '0', '发送人的名字', '收卡人的名字', '吉日迁居万事如意，良辰安宅百年遂心。蜜蜂筑了新巢，酿了一罐甜甜的蜂蜜；燕子垒了新窝，孵了一群可爱的小燕；蚂蚁挖了新洞，备了一仓过冬的粮食；你也搬新居了，欣欣向荣新面貌，祝贺你万事随心意！', '204', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('169', '0', '发送人的名字', '收卡人的名字', '十年修得同船渡，百年修得共枕眠。于茫茫人海中找到她，相知而相爱，由相爱而更加相知，分明是千年前的一段缘，人们常说的神仙眷侣就是你们了！祝你俩幸福美满，共谐连理，相爱年年岁岁，相知岁岁年年！结婚一周年快乐！', '205', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('170', '0', '发送人的名字', '收卡人的名字', '想要和你光着脚丫子，有细沙轻抚，看海浪调皮，阳光在你嘴角划过美丽的光圈，这就是我想要的幸福。执子之手，与子偕老，细水长流不过如此，我只想带你把风景看透，然后温馨相守。', '206', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('171', '0', '发送人的名字', '收卡人的名字', '想起你的时候，心里甜甜的；看不见你的时候，心里酸酸的；拥抱你的时候，幸福的滋味充盈四周。世界这么大，还好遇见你，因为爱你，我如此幸福。', '207', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('172', '0', '发送人的名字', '收卡人的名字', '遇见你的第一眼，我想起那些很美的句子，愿麦子和麦子长在一起，愿河流与河流流归一处，愿有情人终成眷属。你的微笑绽放，我的爱意萌芽，不知不觉，对你的眷念早已心醉神迷，浩荡成一片绿荫。', '208', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('173', '0', '发送人的名字', '收卡人的名字', '这世上所有的情话都词不达意，这世上所有的鲜花都配不上你，唯有你能让我着迷，只要有你，我愿把最美的春天送给你。愿你幸福快乐每一天！', '209', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('174', '0', '发送人的名字', '收卡人的名字', '暖暖的阳光照耀你，柔柔的清风抚摸你，阵阵的细雨呼唤你，静静的夜里在想你，情人节来临，送你吉祥如意，愿你笑口常开！', '210', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('175', '0', '发送人的名字', '收卡人的名字', '玫瑰香香，情人黏黏，情话甜甜，情歌绵绵；花灯灿灿，礼花点点，好运连连，好梦圆圆。情人节喜逢元宵节，喜鹊登枝蝴蝶成双鸳鸯成对双喜临门祝双节快乐，合家团团圆圆，甜甜蜜蜜，开开心心，幸幸福福。', '211', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('176', '0', '发送人的名字', '收卡人的名字', '有了太阳地球才会转，有了月亮星光才如此灿烂，有了你我的世界才如此浪漫。因为有你，一切都是甜蜜。每分每刻，我都比上一秒更爱你！情人节快乐！', '212', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('177', '0', '发送人的名字', '收卡人的名字', '白天我们思念，夜晚我们梦见，约会我们不断，情缘我们无限，相见我们恨晚，热恋我们不倦，爱心我们互献，祝福我们永远。情人节快乐！', '213', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('178', '0', '发送人的名字', '收卡人的名字', '难以忘记第一次与你相遇，你的眼眸深深映在我脑海里，想你是我今生的痛苦，你能医治我的心病吗？洞庭湖的水，绿油油，咱俩的爱情才开头，你是我的心，你是我的肝，你是我心中的四分之三！', '214', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('179', '0', '发送人的名字', '收卡人的名字', '相知是一种宿命，心灵的交汇让我们有诉不尽的浪漫情怀；相守是一种承诺，人世轮回中，永远铭记我们这段美丽的爱情故事！ 愿我们的爱情永远像幽淡的清茶、香浓的咖啡、浪漫的红酒、热烈的伏特加那样多姿多彩，让人回味……', '215', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('180', '0', '发送人的名字', '收卡人的名字', '我喜欢生命只有单纯的盼望，只有一种安定和缓慢的成长，喜欢岁月漂洗过的颜色，喜欢那用心唱出来的歌，更喜欢和这个看贺卡的你做一生一世的伴侣。路是一步一步走出来的。爱是一点一点换回来的。人生也是这样一页一页真真实实活下去的！我珍惜我的人生，更加珍惜你！我的爱人！', '216', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('181', '0', '发送人的名字', '收卡人的名字', '遇见你是偶然，喜欢你不是突然，爱上你却是理所当然，天天想你是习惯成自然。渐渐明白，你是我一切的出发点，想从你身边走出一条射线，却画了一个圆，你就是我生命中的圆心，让我照顾你好吗？', '217', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('182', '0', '发送人的名字', '收卡人的名字', '遇见了，不能不让我相信这是宿命。梦见了，才知这份情深。无力抗拒，我不求天长地久，只求生生世世轮回里有你有我。很高兴遇见你，上天给了我双份的幸运，于是我们得以重逢，感激！', '218', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('183', '0', '发送人的名字', '收卡人的名字', '爱你一万年，夸张！爱你五千年，无望！爱你一千年，荒唐！爱你一百年，太长！我决定，爱你一生一世！我愿意把我的一生全部都交给你，爱你不变是我今生永远的誓言。情人节，无需浪漫的语言，只愿我能住进你的心里。', '219', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('184', '0', '发送人的名字', '收卡人的名字', '感谢命运在我的生命里邀约了你，爱你是我一天最重要的事，想你是我一天最快乐的事，看你是我一天最享受的事，爱你想你看你是我一辈子都要做的事，你是我最简单的快乐，认识你是我这辈子最美好的事，我真的真的好爱你。', '220', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('185', '0', '发送人的名字', '收卡人的名字', '蝶儿飞舞将白色的花朵眷恋，柔情就是相依相恋；白色的云儿衬托着蓝天，痴情就是约定永远；白雾弥漫着巍峨青山，幸福就是如梦如幻；爱的誓言传遍万水千山，耐得住空间的阻隔，经得起时间的考验，一片深情寄存你心田。情人节，祝心爱的你笑容甜甜！', '221', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('186', '0', '发送人的名字', '收卡人的名字', '风是透明的，雨是滴答的，云是流动的，歌是自由的，爱是用心的，恋是疯狂的，天是永恒的，你是难忘的。原谅我将你的手机号码告诉了一个陌生人，他叫丘比特，请他要帮我告诉你：我心喜欢你，我心在乎你，我心等待你。', '222', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('187', '0', '发送人的名字', '收卡人的名字', '因为有星星，夜才不会黑暗；因为有天空，大海才一片蔚蓝；因为有梦，生命充满期盼；因为有您，成长充满笑颜。作业本上一页页的批语奠定了未来的一步步脚印，让清风捎去满心的祝福与感激，期待和您重逢的那刻！', '223', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('188', '0', '发送人的名字', '收卡人的名字', '漫漫人生路，总会遇见几许离愁，每一个人走来都有不同感受，惊喜，不舍，难言，是好是坏，有时在于几分机缘与自我抉择。怀念有你陪伴的日子，让我们用自信的笑脸，去迎接人生的狂喜与挫败，时间打不败离别，珍重吧朋友！', '224', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('189', '0', '发送人的名字', '收卡人的名字', '做过最美的梦里有你，宁愿不醒来。我不怕车水马龙的忙碌，我不怕没你陪我看星空。只要有你的消息，便睡得香甜踏实，原来对我最管用的安眠药是你的名字。不想起床，只愿多留恋你一秒，早安宝贝儿！', '225', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('190', '0', '发送人的名字', '收卡人的名字', '上辈子一定暴扁过周公，现在才变瞌睡虫，不想不想上班，周末的阳光比美女抢眼。人生十分短暂，清醒的时光用来慵懒，微醺的间隙用来狂欢，半梦半醒的片刻用来尽情怀恋。嗨，不想上班，你也是吗？', '226', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('191', '0', '发送人的名字', '收卡人的名字', '巫婆说这世上谁都逃不掉“长大”的魔咒，这世界需要拯救，超人需要帮手，奥特曼与小怪兽已经快变成好朋友。我不想上学，想让痛快多一秒，思考时间不会少，顺性而为是一种天赋，我的年轻我骄傲！', '227', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('192', '0', '发送人的名字', '收卡人的名字', '我在意你的心里有没有下雨，那样的话，如果恰巧你心里有我，我那颗爱你的心就不会淋湿。', '228', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '1', '12');
