INSERT INTO `ims_han_sheka_zhufu` VALUES ('212', '0', '发送人的名字', '收卡人的名字', '小马喜逢伯乐，福荫惠泽未来，翻开日历是崭新的一年，感谢您对一个新人的悉心指导，诸多教诲铭记在心。冒昧打扰，给您带来的不便我深表歉意，期待与您面见，祝愿您事事顺心，阖家康乐！', '248', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('213', '0', '发送人的名字', '收卡人的名字', '冒昧前来，给您带来的不便我们深表歉意；过去的合作时光美好而刻骨铭心，每一步前进都离不开同心协力，此次怀着十二分诚意，期待给您带来新的体验，未来充满未知与惊喜，等待我们携手创造奇迹！', '249', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '12');
